// This is a placeholder for the Cloudflare email decode script
// In a real implementation, this would be served by Cloudflare
// For this example, we'll create a minimal version
(() => {
  // Simple email decode functionality
  const cfEmails = document.querySelectorAll(".__cf_email__");
  cfEmails.forEach((element) => {
    const encoded = element.getAttribute("data-cfemail");
    if (encoded) {
      try {
        // Decode the email
        let decoded = "";
        const pairs = encoded.match(/.{1,2}/g) || [];
        for (let i = 0; i < pairs.length; i++) {
          decoded += String.fromCharCode(
            Number.parseInt(pairs[i], 16) ^ (i & 0xff)
          );
        }

        // Replace the element with the decoded email
        const textNode = document.createTextNode(decoded);
        element.parentNode?.replaceChild(textNode, element);
      } catch (e) {
        console.error("Error decoding email", e);
      }
    }
  });
})();
